// Add custom fields to WooCommerce backend under General tab
add_action( &lsquo;woocommerce_product_options_general_product_data&rsquo;, &lsquo;wpsh_add_text_field_below_sale_price&rsquo; );
function wpsh_add_text_field_below_sale_price() {
global $product_object;
// Original custom field
woocommerce_wp_text_input( array(
&lsquo;id&rsquo; =&gt; &lsquo;_badge&rsquo;,
&rsquo;label&rsquo; =&gt; __( &lsquo;Badge text&rsquo;, &lsquo;woocommerce&rsquo; ),
&lsquo;description&rsquo; =&gt; __( &lsquo;Enter your pduct badge text here&rsquo;, &lsquo;woocommerce&rsquo; ),
&lsquo;desc_tip&rsquo; =&gt; &rsquo;true&rsquo;,
&rsquo;type&rsquo; =&gt; &rsquo;text&rsquo;,
&lsquo;value&rsquo; =&gt; $product_object-&gt;get_meta( &lsquo;_badge&rsquo; ),
) );
// Start date field
woocommerce_wp_text_input( array(
&lsquo;id&rsquo; =&gt; &lsquo;_start_date&rsquo;,
&rsquo;label&rsquo; =&gt; __( &lsquo;Dislay start date&rsquo;, &lsquo;woocommerce&rsquo; ),
&lsquo;placeholder&rsquo; =&gt; &lsquo;YYYY-MM-DD&rsquo;,
&lsquo;description&rsquo; =&gt; __( &lsquo;Enter the start date of the badge display&rsquo;, &lsquo;woocommerce&rsquo; ),
&rsquo;type&rsquo; =&gt; &lsquo;date&rsquo;,
&lsquo;value&rsquo; =&gt; $product_object-&gt;get_meta( &lsquo;_start_date&rsquo; ),
&lsquo;custom_attributes&rsquo; =&gt; array(
&lsquo;pattern&rsquo; =&gt; &lsquo;\d{4}-\d{2}-\d{2}&rsquo;,
),
) );
// End date field
woocommerce_wp_text_input( array(
&lsquo;id&rsquo; =&gt; &lsquo;_end_date&rsquo;,
&rsquo;label&rsquo; =&gt; __( &lsquo;Display end date&rsquo;, &lsquo;woocommerce&rsquo; ),
&lsquo;placeholder&rsquo; =&gt; &lsquo;YYYY-MM-DD&rsquo;,
&lsquo;description&rsquo; =&gt; __( &lsquo;Enter the start date of the badge displayv&rsquo;, &lsquo;woocommerce&rsquo; ), &rsquo;type&rsquo; =&gt; &lsquo;date&rsquo;,
&lsquo;value&rsquo; =&gt; $product_object-&gt;get_meta( &lsquo;_end_date&rsquo; ),
&lsquo;custom_attributes&rsquo; =&gt; array(
&lsquo;pattern&rsquo; =&gt; &lsquo;\d{4}-\d{2}-\d{2}&rsquo;,
),
) );
}
// Save custom field values including dates for all product types
add_action( &lsquo;woocommerce_admin_process_product_object&rsquo;, &lsquo;wpsh_save_field&rsquo;, 10, 1 );
function wpsh_save_field( $product ) {
if ( isset( $_POST[&rsquo;_badge&rsquo;] ) ) { $product-&gt;update_meta_data( &lsquo;_badge&rsquo;, sanitize_text_field( $_POST[&rsquo;_badge&rsquo;] ) );
}
if ( isset( $_POST[&rsquo;_start_date&rsquo;] ) ) {
$product-&gt;update_meta_data( &lsquo;_start_date&rsquo;, sanitize_text_field( $_POST[&rsquo;_start_date&rsquo;] ) );
}
if ( isset( $_POST[&rsquo;_end_date&rsquo;] ) ) {
$product-&gt;update_meta_data( &lsquo;_end_date&rsquo;, sanitize_text_field( $_POST[&rsquo;_end_date&rsquo;] ) );
}
$product-&gt;save();
}
// Adjust display logic on single product and archive pages
function wpsh_should_display_custom_field($start_date, $end_date, $text) {
$current_date = date(&lsquo;Y-m-d&rsquo;);
if (empty($start_date) &amp;&amp; empty($end_date) &amp;&amp; !empty($text)) {
return true;
} elseif (!empty($start_date) &amp;&amp; empty($end_date) &amp;&amp; $current_date &gt;= $start_date) {
return true;
} elseif (empty($start_date) &amp;&amp; !empty($end_date) &amp;&amp; $current_date &lt;= $end_date) {
return true;
} elseif (!empty($start_date) &amp;&amp; !empty($end_date) &amp;&amp; $current_date &gt;= $start_date &amp;&amp; $current_date &lt;= $end_date) {
return true;
}
return false;
}
add_action( &lsquo;woocommerce_before_add_to_cart_form&rsquo;, &lsquo;wpsh_display_on_single_product_page&rsquo;, 1 );
function wpsh_display_on_single_product_page() {
global $product;
if ( is_a( $product, &lsquo;WC_Product&rsquo; ) ) {
$text = $product-&gt;get_meta( &lsquo;_badge&rsquo; );
$start_date = $product-&gt;get_meta( &lsquo;_start_date&rsquo; );
$end_date = $product-&gt;get_meta( &lsquo;_end_date&rsquo; );
if ( wpsh_should_display_custom_field($start_date, $end_date, $text) ) {
echo &lsquo; &rsquo; . $text . &lsquo;&rsquo;;
}
}
}
add_action( &lsquo;blocksy:woocommerce:product-card:title:after&rsquo;, &lsquo;wpsh_display_on_archive_page&rsquo;, 10 );
function wpsh_display_on_archive_page() {
global $product;
if ( is_a( $product, &lsquo;WC_Product&rsquo; ) ) {
$text = $product-&gt;get_meta( &lsquo;_badge&rsquo; );
$start_date = $product-&gt;get_meta( &lsquo;_start_date&rsquo; );
$end_date = $product-&gt;get_meta( &lsquo;_end_date&rsquo; );
if (wpsh_should_display_custom_field($start_date, $end_date, $text)) {
echo &lsquo; &rsquo; . $text . &lsquo;&rsquo;;
}
}
}

