&gt; &lt;style&gt; &gt; body { display: none; } &lt;/style&gt; &lt;script&gt; function validatePassword() { var password = prompt(&#34;Please enter the security key:&#34;); &gt; if (password !== &#34;200&#34;) { alert(&#34;Incorrect security key. Access denied.&#34;); document.body.innerHTML = &#34;&#34;; } else { // If the password is correct, display the body content document.body.style.display = &#34;block&#34;; } } &lt;/script&gt;&lt;body onload=&#34;validatePassword()&#34;&gt;&lt;/body&gt; V2 (security enhanced)
&gt; &lt;style&gt; &gt; body { display: none; } &lt;/style&gt; &lt;script&gt; function validatePassword() { var allowedAttempts = 3; // Set the maximum number of attempts var password = &#34;200&#34;; // Set the correct password var attempts = 0; &gt; function clearBody() { document.body.innerHTML = &#34;&#34;; } &gt; function displayContent() { document.body.style.display = &#34;block&#34;; } &gt; function showAccessDenied() { alert(&#34;Incorrect security key. Access denied.&#34;); attempts++; if (attempts &gt;= allowedAttempts) { alert(&#34;Maximum attempts reached. Access denied.&#34;); clearBody(); } else { validatePassword(); // Prompt for password again } } &gt; var enteredPassword = prompt(&#34;Please enter the security key:&#34;); &gt; if (enteredPassword !== password) { showAccessDenied(); } else { // If the password is correct, display the body content displayContent(); } } &lt;/script&gt;&lt;body onload=&#34;validatePassword()&#34;&gt; &gt; &lt;/body&gt;
