document.addEventListener("DOMContentLoaded",()=>{const n=document.querySelectorAll(".gallery-item img"),e=document.getElementById("lightbox"),s=document.getElementById("lightbox-img"),o=document.getElementById("lightbox-caption"),i=document.querySelector(".close");n.forEach(t=>{t.addEventListener("click",()=>{s.src=t.src;const n=t.parentElement.querySelector(".image-caption").innerHTML;o.innerHTML=n,e.style.display="block",document.body.style.overflow="hidden"})});const t=()=>{e.style.display="none",document.body.style.overflow="auto"};i.addEventListener("click",t),e.addEventListener("click",n=>{n.target===e&&t()}),document.addEventListener("keydown",n=>{n.key==="Escape"&&e.style.display==="block"&&t()})})